import React, { useState } from 'react';
import { useTheme } from './ThemeContext';
import ThemeSwitch from './ThemeSwitch';

const NAV_LINKS = [
  { key: 'clients', label: 'Clients' },
  { key: 'produits', label: 'Produits' },
  { key: 'factures', label: 'Factures' },
  { key: 'stats', label: 'Statistiques' },
  { key: 'settings', label: 'Paramètres' },
  { key: 'printcalc', label: 'Impression 3D' },
];

export default function NavBar({ page, setPage }) {
  const theme = useTheme();
  const [open, setOpen] = useState(false);

  const navStyle = {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'space-between',
    background: theme.card,
    color: theme.text,
    padding: '0 16px',
    height: 56,
    borderBottom: `1px solid ${theme.border}`,
    position: 'sticky',
    top: 0,
    zIndex: 100,
  };

  // Couleur de fond opaque pour le menu mobile
  const menuBg = theme.mode === 'dark' ? '#222' : '#fff';
  const menuBorder = theme.mode === 'dark' ? '#444' : '#e0e0e0';

  return (
    <nav style={navStyle}>
      <div style={{display: 'flex', alignItems: 'center', gap: 12}}>
        <button
          className="burger"
          aria-label="Menu"
          onClick={() => setOpen(o => !o)}
          style={{
            display: 'none',
            background: theme.mode === 'dark' ? '#333' : '#ddd',
            border: `1px solid ${theme.mode === 'dark' ? '#555' : '#ccc'}`,
            fontSize: 28,
            cursor: 'pointer',
            marginRight: 8,
            padding: 4,
            borderRadius: 4,
          }}
        >☰</button>
        <span style={{ fontWeight: 700, fontSize: 20 }}>Billing 3D</span>
      </div>
      <div className="nav-links" style={{ display: 'flex', alignItems: 'center', gap: 24 }}>
        {NAV_LINKS.map(link => (
          <button
            key={link.key}
            onClick={() => setPage(link.key)}
            style={{
              background: 'none',
              border: 'none',
              color: page === link.key ? theme.primary : theme.text,
              fontWeight: page === link.key ? 700 : 400,
              fontSize: 16,
              cursor: 'pointer',
              padding: 4,
              borderBottom: page === link.key ? `2px solid ${theme.primary}` : 'none',
              transition: 'color 0.2s'
            }}
          >{link.label}</button>
        ))}
      </div>
      <div style={{ marginLeft: 8 }}>
        <ThemeSwitch />
      </div>
      {/* Menu mobile */}
      {open && (
        <div
          style={{
            position: 'fixed',
            top: 56,
            left: 0,
            width: '100vw',
            background: menuBg,
            color: theme.text,
            borderTop: `1px solid ${menuBorder}`,
            borderBottom: `2px solid ${menuBorder}`,
            zIndex: 200,
            padding: 16,
            display: 'flex',
            flexDirection: 'column',
            gap: 18,
            boxShadow: theme.mode === 'dark' ? '0 2px 16px #000b' : '0 2px 12px #bbb8',
            opacity: 1,
          }}
        >
          {NAV_LINKS.map(link => (
            <button
              key={link.key}
              onClick={() => { setPage(link.key); setOpen(false); }}
              style={{
                background: 'none',
                border: 'none',
                color: page === link.key ? theme.primary : theme.text,
                fontWeight: page === link.key ? 700 : 400,
                fontSize: 18,
                textAlign: 'left',
                padding: 6,
                borderBottom: page === link.key ? `2px solid ${theme.primary}` : 'none',
                transition: 'color 0.2s',
              }}
            >{link.label}</button>
          ))}
          <div style={{marginTop: 16}}><ThemeSwitch /></div>
        </div>
      )}
      <style>{`
        @media (max-width: 900px) {
          .nav-links { display: none !important; }
          .burger { display: block !important; }
        }
        @media (min-width: 901px) {
          .nav-links { display: flex !important; }
          .burger { display: none !important; }
        }
      `}</style>
    </nav>
  );
}
