import React, { useEffect, useState } from 'react';

export default function StatsPage() {
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    Promise.all([
      fetch('http://localhost:5000/api/clients').then(res => res.json()),
      fetch('http://localhost:5000/api/produits').then(res => res.json()),
      fetch('http://localhost:5000/api/factures').then(res => res.json())
    ])
      .then(([clients, produits, factures]) => {
        const totalFactures = factures.reduce((sum, f) => sum + (f.montant || 0), 0);
        setStats({
          clients: clients.length,
          produits: produits.length,
          factures: factures.length,
          totalFactures
        });
      })
      .catch(err => setError(err.message))
      .finally(() => setLoading(false));
  }, []);

  if (loading) return <div>Chargement des statistiques...</div>;
  if (error) return <div style={{ color: 'red' }}>Erreur : {error}</div>;

  return (
    <div style={{ maxWidth: 600, margin: '0 auto', padding: 32 }}>
      <h2>Statistiques</h2>
      <ul style={{ fontSize: 18, lineHeight: 2 }}>
        <li><b>Nombre de clients :</b> {stats.clients}</li>
        <li><b>Nombre de produits :</b> {stats.produits}</li>
        <li><b>Nombre de factures :</b> {stats.factures}</li>
        <li><b>Total facturé :</b> {stats.totalFactures.toFixed(2)} €</li>
      </ul>
    </div>
  );
}
