import React from 'react';

export default function Notification({ message, type, onClose }) {
  if (!message) return null;
  return (
    <div style={{
      position: 'fixed',
      bottom: 24,
      right: 24,
      background: type === 'success' ? '#28a745' : '#dc3545',
      color: 'white',
      padding: '16px 32px',
      borderRadius: 8,
      boxShadow: '0 2px 8px rgba(0,0,0,0.15)',
      zIndex: 1000,
      fontWeight: 'bold',
      fontSize: 18
    }}>
      {message}
      <button onClick={onClose} style={{ marginLeft: 24, background: 'transparent', color: 'white', border: 'none', fontSize: 20, cursor: 'pointer' }}>Ã—</button>
    </div>
  );
}
