import React, { useEffect, useState } from "react";

function PrintCalcHistory({ onBack }) {
  const [history, setHistory] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch("/api/calcul-historique")
      .then((res) => res.json())
      .then((data) => {
        setHistory(data);
        setLoading(false);
      });
  }, []);

  const handleDelete = (id) => {
    if (!window.confirm("Supprimer ce calcul ?")) return;
    fetch(`/api/calcul-historique/${id}`, { method: "DELETE" })
      .then((res) => res.json())
      .then(() => setHistory(history.filter((h) => h.id !== id)));
  };

  return (
    <div style={{ maxWidth: 900, margin: "40px auto", background: "#fff", borderRadius: 8, padding: 24 }}>
      <h2>Historique des calculs impression 3D</h2>
      <button onClick={onBack} style={{ marginBottom: 16 }}>Retour</button>
      {loading ? (
        <div>Chargement…</div>
      ) : history.length === 0 ? (
        <div>Aucun calcul enregistré.</div>
      ) : (
        <table style={{ width: "100%", borderCollapse: "collapse" }}>
          <thead>
            <tr style={{ background: "#f0f0f0" }}>
              <th>Date</th>
              <th>Nom produit</th>
              <th>Temps (h)</th>
              <th>Poids (g)</th>
              <th>Marge (%)</th>
              <th>Prix final (€)</th>
              <th>Prix arrondi (€)</th>
              <th>Description</th>
              <th></th>
            </tr>
          </thead>
          <tbody>
            {history.map((h) => (
              <tr key={h.id}>
                <td>{new Date(h.date).toLocaleString()}</td>
                <td>{h.nom_produit}</td>
                <td>{h.temps_impression}</td>
                <td>{h.poids_filament}</td>
                <td>{h.marge}</td>
                <td>{h.prix_final}</td>
                <td>{h.prix_arrondi}</td>
                <td>{h.description}</td>
                <td>
                  <button onClick={() => handleDelete(h.id)} style={{ color: "red", fontWeight: "bold", border: "none", background: "none", fontSize: 18, cursor: "pointer" }} title="Supprimer">✖</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default PrintCalcHistory;
