import React, { useState } from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import App from './App';
import LoginPage from './LoginPage';
import PasswordResetRequest from './PasswordResetRequest';
import PasswordResetForm from './PasswordResetForm';
import * as serviceWorkerRegistration from './serviceWorkerRegistration';
import reportWebVitals from './reportWebVitals';

function RootRouter() {
  const [route, setRoute] = useState(() => {
    if (window.location.pathname.startsWith('/password-reset/')) return 'reset';
    if (window.location.pathname === '/login') return 'login';
    return 'app';
  });
  const [resetToken, setResetToken] = useState(() => {
    const match = window.location.pathname.match(/\/password-reset\/(.+)$/);
    return match ? match[1] : '';
  });
  const [user, setUser] = useState(null);

  // Navigation helpers
  const goLogin = () => { window.history.pushState({}, '', '/login'); setRoute('login'); };
  const goApp = () => { window.history.pushState({}, '', '/'); setRoute('app'); };
  const goReset = (token) => { window.history.pushState({}, '', `/password-reset/${token}`); setResetToken(token); setRoute('reset'); };

  if (route === 'login')
    return <LoginPage onLogin={data => { setUser(data); goApp(); }} onShowReset={() => setRoute('reset-request')} />;
  if (route === 'reset-request')
    return <PasswordResetRequest onBack={goLogin} />;
  if (route === 'reset')
    return <PasswordResetForm token={resetToken} onBack={goLogin} />;
  // Si pas connecté, redirige vers login
  if (!user) return <LoginPage onLogin={data => { setUser(data); goApp(); }} onShowReset={() => setRoute('reset-request')} />;
  return <App user={user} />;
}

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  <React.StrictMode>
    <RootRouter />
  </React.StrictMode>
);

// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: https://cra.link/PWA
serviceWorkerRegistration.unregister();

// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();
