import React, { useState, useEffect } from 'react';

export default function EditInvoiceForm({ facture, clients, onSave, onCancel }) {
  const [clientId, setClientId] = useState(facture.client_id);
  const [date, setDate] = useState(facture.date);
  const [montant, setMontant] = useState(facture.montant);
  const [statut, setStatut] = useState(facture.statut);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (clients.length > 0 && !clientId) {
      setClientId(clients[0].id);
    }
  }, [clients, clientId]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    try {
      const response = await fetch(`http://localhost:5000/api/factures/${facture.id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ client_id: clientId, date, montant: parseFloat(montant), statut })
      });
      if (!response.ok) throw new Error("Erreur lors de la modification de la facture");
      const updated = await response.json();
      onSave(updated);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ marginBottom: 24, background: '#fffbe8', padding: 16, borderRadius: 8, border: '1px solid #ffe08a' }}>
      <h3>Modifier la facture</h3>
      <div style={{ display: 'flex', gap: 12, flexWrap: 'wrap' }}>
        <select required value={clientId} onChange={e => setClientId(e.target.value)} style={{ flex: 2 }}>
          {clients.map(client => (
            <option key={client.id} value={client.id}>{client.nom}</option>
          ))}
        </select>
        <input required type="date" value={date} onChange={e => setDate(e.target.value)} style={{ flex: 2 }} />
        <input required type="number" min="0" step="0.01" placeholder="Montant (€)" value={montant} onChange={e => setMontant(e.target.value)} style={{ flex: 1 }} />
        <select value={statut} onChange={e => setStatut(e.target.value)} style={{ flex: 1 }}>
          <option value="En attente">En attente</option>
          <option value="Payée">Payée</option>
          <option value="Annulée">Annulée</option>
        </select>
        <button type="submit" disabled={loading} style={{ minWidth: 120, background: '#ffc107', color: '#222', border: 'none', borderRadius: 4 }}>
          {loading ? 'Modification...' : 'Enregistrer'}
        </button>
        <button type="button" onClick={onCancel} style={{ minWidth: 100, background: '#eee', color: '#222', border: 'none', borderRadius: 4 }}>
          Annuler
        </button>
      </div>
      {error && <div style={{ color: 'red', marginTop: 8 }}>{error}</div>}
    </form>
  );
}
