import React, { useState } from 'react';
import ClientsPage from './ClientsPage';
import ProductsPage from './ProductsPage';
import InvoicesPage from './InvoicesPage';

export default function App() {
  const [page, setPage] = useState('clients');

  return (
    <div>
      <header style={{ background: '#222', color: 'white', padding: 24, marginBottom: 32 }}>
        <h1 style={{ margin: 0, textAlign: 'center' }}>SP3D Billing WebApp</h1>
        <nav style={{ marginTop: 16, textAlign: 'center' }}>
          <button onClick={() => setPage('clients')} style={{ marginRight: 16, padding: '8px 24px', borderRadius: 6, border: 'none', background: page === 'clients' ? '#ffa500' : '#fff', color: page === 'clients' ? '#222' : '#222', fontWeight: 'bold', cursor: 'pointer' }}>Clients</button>
          <button onClick={() => setPage('produits')} style={{ marginRight: 16, padding: '8px 24px', borderRadius: 6, border: 'none', background: page === 'produits' ? '#ffa500' : '#fff', color: page === 'produits' ? '#222' : '#222', fontWeight: 'bold', cursor: 'pointer' }}>Produits</button>
          <button onClick={() => setPage('factures')} style={{ padding: '8px 24px', borderRadius: 6, border: 'none', background: page === 'factures' ? '#ffa500' : '#fff', color: page === 'factures' ? '#222' : '#222', fontWeight: 'bold', cursor: 'pointer' }}>Factures</button>
        </nav>
      </header>
      {page === 'clients' && <ClientsPage />}
      {page === 'produits' && <ProductsPage />}
      {page === 'factures' && <InvoicesPage />}
    </div>
  );
}
