import React, { useState, useEffect } from 'react';

export default function AddInvoiceForm({ clients, onAdd }) {
  const [clientId, setClientId] = useState('');
  const [date, setDate] = useState('');
  const [selectedProducts, setSelectedProducts] = useState([]);
  const [allProducts, setAllProducts] = useState([]);
  const [statut, setStatut] = useState('En attente');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (clients.length > 0 && !clientId) {
      setClientId(clients[0].id);
    }
    fetch('http://localhost:5000/api/produits')
      .then(res => res.json())
      .then(setAllProducts);
  }, [clients, clientId]);

  const montant = selectedProducts.reduce((sum, id) => {
    const prod = allProducts.find(p => p.id === parseInt(id));
    return sum + (prod ? prod.prix : 0);
  }, 0);

  const handleProductChange = (e) => {
    const options = Array.from(e.target.options);
    setSelectedProducts(options.filter(o => o.selected).map(o => o.value));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    try {
      const response = await fetch('http://localhost:5000/api/factures', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ client_id: clientId, date, montant, statut, produits: selectedProducts })
      });
      if (!response.ok) throw new Error("Erreur lors de l'ajout de la facture");
      const facture = await response.json();
      onAdd(facture);
      setClientId(''); setDate(''); setSelectedProducts([]); setStatut('En attente');
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ marginBottom: 24, background: 'transparent', padding: 16, borderRadius: 8 }}>
      <h3>Ajouter une facture</h3>
      <div style={{ display: 'flex', gap: 12, flexWrap: 'wrap' }}>
        <select required value={clientId} onChange={e => setClientId(e.target.value)} style={{ flex: 2 }}>
          {clients.map(client => (
            <option key={client.id} value={client.id}>{client.nom}</option>
          ))}
        </select>
        <input required type="date" value={date} onChange={e => setDate(e.target.value)} style={{ flex: 2 }} />
        <select multiple required value={selectedProducts} onChange={handleProductChange} style={{ flex: 3, minHeight: 38 }}>
          {allProducts.map(prod => (
            <option key={prod.id} value={prod.id}>{prod.nom} ({prod.prix.toFixed(2)} €)</option>
          ))}
        </select>
        <input type="text" value={montant.toFixed(2)} readOnly style={{ flex: 1, background: '#eee' }} placeholder="Montant (€)" />
        <select value={statut} onChange={e => setStatut(e.target.value)} style={{ flex: 1 }}>
          <option value="En attente">En attente</option>
          <option value="Payée">Payée</option>
          <option value="Annulée">Annulée</option>
        </select>
        <button type="submit" disabled={loading} style={{ minWidth: 120 }}>
          {loading ? 'Ajout...' : 'Ajouter'}
        </button>
      </div>
      {error && <div style={{ color: 'red', marginTop: 8 }}>{error}</div>}
    </form>
  );
}
