import React, { useState } from "react";

function PrintCalc() {
  const [form, setForm] = useState({ temps_impression: "", poids_filament: "", marge_specifique: "" });
  const [result, setResult] = useState(null);
  const [error, setError] = useState("");

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setError("");
    setResult(null);
    fetch("/api/calcul-impression", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        temps_impression: form.temps_impression,
        poids_filament: form.poids_filament,
        marge_specifique: form.marge_specifique || undefined,
      }),
    })
      .then((res) => res.json())
      .then(setResult)
      .catch(() => setError("Erreur lors du calcul."));
  };

  return (
    <div style={{ maxWidth: 500, margin: "40px auto", padding: 24, background: "#fff", borderRadius: 8 }}>
      <h2>Calcul d'impression 3D</h2>
      <form onSubmit={handleSubmit}>
        <label>Temps d'impression (heures) :
          <input type="number" step="0.01" name="temps_impression" value={form.temps_impression} onChange={handleChange} required style={{ width: "100%" }} />
        </label><br /><br />
        <label>Poids filament (g) :
          <input type="number" step="0.01" name="poids_filament" value={form.poids_filament} onChange={handleChange} required style={{ width: "100%" }} />
        </label><br /><br />
        <label>Marge sp√©cifique (%) :
          <input type="number" step="0.01" name="marge_specifique" value={form.marge_specifique} onChange={handleChange} style={{ width: "100%" }} />
        </label><br /><br />
        <button type="submit">Calculer</button>
      </form>
      {error && <div style={{ color: "red", marginTop: 10 }}>{error}</div>}
      {result && (
        <div style={{ marginTop: 24 }}>
          <h4>R√©sultat :</h4>
          <ul>
            <li>Co√ªt filament : {result.cout_filament} ‚Ç¨</li>
            <li>Co√ªt machine : {result.cout_machine} ‚Ç¨</li>
            <li>Co√ªt total (hors marge) : {result.cout_total} ‚Ç¨</li>
            <li>Marge appliqu√©e : {result.marge} %</li>
            <li><b>Prix final : {result.prix_final} ‚Ç¨</b></li>
          </ul>
        </div>
      )}
    </div>
  );
}

export default PrintCalc;
