import React, { useEffect, useState } from 'react';
import ProductTable from './ProductTable';
import AddProductForm from './AddProductForm';
import EditProductForm from './EditProductForm';

export default function ProductsPage() {
  const [produits, setProduits] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [editingProduct, setEditingProduct] = useState(null);

  useEffect(() => {
    fetch('http://localhost:5000/api/produits')
      .then(res => res.json())
      .then(setProduits)
      .catch(err => setError(err.message))
      .finally(() => setLoading(false));
  }, []);

  const handleAddProduct = (produit) => {
    setProduits(prev => [...prev, produit]);
  };

  const handleDeleteProduct = async (id) => {
    if (!window.confirm('Supprimer ce produit ?')) return;
    try {
      const response = await fetch(`http://localhost:5000/api/produits/${id}`, { method: 'DELETE' });
      if (!response.ok) throw new Error('Erreur lors de la suppression');
      setProduits(prev => prev.filter(p => p.id !== id));
    } catch (err) {
      alert(err.message);
    }
  };

  const handleEditProduct = (produit) => {
    setEditingProduct(produit);
  };

  const handleSaveEdit = (updatedProduct) => {
    setProduits(prev => prev.map(p => p.id === updatedProduct.id ? updatedProduct : p));
    setEditingProduct(null);
  };

  const handleCancelEdit = () => {
    setEditingProduct(null);
  };

  if (loading) return <div>Chargement des produits...</div>;
  if (error) return <div style={{ color: 'red' }}>Erreur : {error}</div>;

  return (
    <div style={{ maxWidth: 900, margin: '0 auto', padding: 24 }}>
      <h2>Liste des produits</h2>
      {editingProduct ? (
        <EditProductForm produit={editingProduct} onSave={handleSaveEdit} onCancel={handleCancelEdit} />
      ) : (
        <AddProductForm onAdd={handleAddProduct} />
      )}
      <ProductTable produits={produits} onDelete={handleDeleteProduct} onEdit={handleEditProduct} />
    </div>
  );
}
