import React, { useState } from 'react';
import ClientsPage from './ClientsPage';
import ProductsPage from './ProductsPage';
import InvoicesPage from './InvoicesPage';
import StatsPage from './StatsPage';
import SettingsPage from './SettingsPage';
import PrintCalc from './PrintCalc';
import { ThemeProvider, useTheme } from './ThemeContext';
import ThemeSwitch from './ThemeSwitch';
import './responsive.css';

function AppContent() {
  const [page, setPage] = useState('clients');
  const { theme } = useTheme();

  return (
    <div>
      <ThemeSwitch />
      <header style={{ background: theme === 'dark' ? '#181818' : '#222', color: 'white', padding: 24, marginBottom: 32 }}>
        <h1 style={{ margin: 0, textAlign: 'center' }}>SP3D Billing WebApp</h1>
        <nav style={{ marginTop: 16, textAlign: 'center' }}>
          <button onClick={() => setPage('clients')} style={navBtnStyle(page === 'clients', theme)}>Clients</button>
          <button onClick={() => setPage('produits')} style={navBtnStyle(page === 'produits', theme)}>Produits</button>
          <button onClick={() => setPage('factures')} style={navBtnStyle(page === 'factures', theme)}>Factures</button>
          <button onClick={() => setPage('stats')} style={navBtnStyle(page === 'stats', theme)}>Statistiques</button>
          <button onClick={() => setPage('printcalc')} style={navBtnStyle(page === 'printcalc', theme)}>Calcul impression 3D</button>
          <button onClick={() => setPage('settings')} style={navBtnStyle(page === 'settings', theme)}>Paramètres</button>
        </nav>
      </header>
      {page === 'clients' && <ClientsPage />}
      {page === 'produits' && <ProductsPage />}
      {page === 'factures' && <InvoicesPage />}
      {page === 'stats' && <StatsPage />}
      {page === 'printcalc' && <PrintCalc />}
      {page === 'settings' && <React.Suspense fallback={<div>Chargement…</div>}><SettingsPage /></React.Suspense>}
    </div>
  );
}

function navBtnStyle(active, theme) {
  return {
    marginRight: 16,
    padding: '8px 24px',
    borderRadius: 6,
    border: 'none',
    background: active ? (theme === 'dark' ? '#ffa500' : '#ffa500') : (theme === 'dark' ? '#333' : '#fff'),
    color: active ? '#222' : (theme === 'dark' ? '#fff' : '#222'),
    fontWeight: 'bold',
    cursor: 'pointer',
    transition: 'background 0.2s',
  };
}

export default function App() {
  return (
    <ThemeProvider>
      <AppContent />
    </ThemeProvider>
  );
}
